import React, {Component} from "react";
// import "./Board.css";
import MessageList from "../MessageList";
import API from "../../utils/API";
import { Col, Row, Container } from "../Grid";
import { List } from "../List";
import Message from "../Message";
import Form from "../Form";
import Card from "../Card";

//props.messages
// const fakeMessages = [
//     {

//         district: "1",
//         title: "title",
//         author: "author",
//         body: "body",
//         category: [0],


//         // title: "title",
//         // name: "name",
//         // category: "category",
//         // message: "message"
//     }
// ];


{/* <h3>{props.title}</h3>
      <h3>{props.name}</h3> 
      <h4>{props.category}</h4> 
      <h4>{props.message}</h4>  */}

      class Board extends Component {


    state = {
        messages: []
      };
      //passvvvvv form_id or whatever message board we are on 
    componentDidMount() {
        this.getSavedMessage();
      }

    getSavedMessage = () => {
        API.getSavedMessage()
          .then(res =>{
            console.log(res.data)
            this.setState({
              messages: res.data
            })}
          )
          .catch(err => console.log(err));
      };

     //This Green Part needs to be turned into message section
     ////////////////////////////////
      handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
          [name]: value
        });
        // console.log("inside change")
      };

    handleMessageSave = id => {
        const message = this.state.message.find(message => message.id === id);

        API.saveMessage({
          // forum_id: resForum._id,
          // user_id: resUser._id,
          content: "",
          date: new Date()
        }).then(() => this.getSavedMessages());
      };


      handleFormSubmit = event => {
        event.preventDefault();
        this.handleMessageSave();
        // this.db.insert();
        console.log("inside sumbit")
      };



      /*
      */

      render() {
        return (
          <Container>

            <Row>
              <Col size="md-12">
              <Card title="Post Content" icon="far fa-book">
                  <Form
                     handleInputChange={this.handleInputChange}
                     handleFormSubmit={this.handleFormSubmit}
                     q={this.state.q}
                    />
              </Card>  

                  <MessageList 
                    messages = {this.state.messages}
                  />

              </Col>
             </Row>

            <Row>
              <Message message={this.state.messages[0]}>

              </Message> 
                 <MessageList title="Results">
                  {this.state.message ? (
                    <List>
                      {this.state.message.map(message => (
                        <Message
                          key={message.id}
                          title={message.title}


                          author={message.author}
                          body={message.body}
                          category={message.category}

                        />
                      ))}
                    </List>
                  ) : (
                    <h2 className="text-center">{this.state.message}</h2>
                  )}
                </MessageList> 

            </Row>

          </Container>
        );
      }
    }

//       const Board = props => (
//     <div>
//         <h3>
//             {props.districtId}
//         </h3>
//         <MessageList district = "2" />
//     </div>
// )
// (header) district ID/name
// body(-messages(message-list))

export default Board;
